var zipped = require('../zippy')
      .zip(['a', 'b', 'c'],[1, 2, 3],['u','v','w']),
    assert = require('assert'),
    util = require('util');

[['a', 1, 'u'], ['b', 2, 'v'], ['c', 3, 'w']].forEach(function (row, i) {
  row.forEach(function (elem, j) {
    assert.equal(elem, zipped[i][j], util.format(
      'zipped[%d][%d] == %d',
      i, j, elem
    ));
  });
});

console.log('Congrats, tests pass!');
